$(document).ready(function () {
    $('.botones-table').append(
        $('<button>', {
            class: 'btn btn-warning',
            "data-mdb-ripple-init": '',
            id: 'btn-exportar-reporte'
        })
            .html('<i class="fas fa-file-excel me-2"></i>Exportar Reporte')
            .on('click', function () {
                $('#modalExport').modal('show');
            })
    );

    let modo = 'mensual';
    let rangoSeleccionado = null;
    const $fechaExport = $('#fechaExport');
    const $titulo = $('#modalExportLabel');

    // --- FUNCI칍N PARA ACTIVAR RANGO ---
    function activarRango() {
        // Si ya existe un daterangepicker, lo destruimos antes
        if ($fechaExport.data('daterangepicker')) {
            $fechaExport.data('daterangepicker').remove();
        }

        $fechaExport.daterangepicker({
            autoUpdateInput: true, // <--- ACTIVADO para autocompletar
            showDropdowns: true,
            startDate: date('Y-m-01'),
            endDate: date('Y-m-d'),
            maxDate: date('Y-m-d'),
            autoUpdateInput: true,
            opens: "center",
            cancelClass: "btn-link",
            locale: {
                format: 'YYYY-MM-DD',
                separator: ' a ',
                applyLabel: 'Aplicar',
                cancelLabel: 'Cerrar',
                fromLabel: 'Desde',
                toLabel: 'Hasta',
                customRangeLabel: 'Rango personalizado',
                daysOfWeek: ["Do", "Lu", "Ma", "Mi", "Ju", "Vi", "Sa"],
                monthNames: [
                    "Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio",
                    "Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"
                ],
                firstDay: 1
            },
            // Limita a 2 meses m치ximo
            isInvalidDate: function (date) {
                const start = this.startDate;
                if (!start) return false;
                const diffMonths = date.diff(start, 'months', true);
                return diffMonths > 2;
            }
        }).on('apply.daterangepicker', function (ev, picker) {
            const start = picker.startDate.format('YYYY-MM-DD');
            const end = picker.endDate.format('YYYY-MM-DD');

            rangoSeleccionado = [start, end];
            $(this).val(start + ' a ' + end); // <--- esto asegura que se vea el valor
        });
        rangoSeleccionado = ($fechaExport.val()).split(' a ');
    }

    // --- DESACTIVAR RANGO ---
    function desactivarRango() {
        if ($fechaExport.data('daterangepicker')) {
            $fechaExport.data('daterangepicker').remove();
        }
        rangoSeleccionado = null;
    }

    // --- MODO MENSUAL ---
    $('#btnMensual').on('click', function () {
        modo = 'mensual';
        $(this).addClass('active');
        $('#btnRango').removeClass('active');
        $titulo.text('EXPORTAR MENSUAL');

        desactivarRango();
        $fechaExport.attr('type', 'month');
        $fechaExport.removeAttr('readonly');
        $fechaExport.val(new Date().toISOString().slice(0, 7)); // YYYY-MM
    });

    // --- MODO RANGO ---
    $('#btnRango').on('click', function () {
        modo = 'rango';
        $(this).addClass('active');
        $('#btnMensual').removeClass('active');
        $titulo.text('EXPORTAR POR RANGO');

        $fechaExport.attr('type', 'button');
        $fechaExport.attr('readonly', '');
        $fechaExport.val('');
        activarRango();
    });

    // --- SHOW PICKER SOLO EN MENSUAL ---
    $fechaExport.on('click', function () {
        if (modo === 'mensual') {
            this.showPicker();
        }
    });

    // --- EXPORTAR ---
    $('#btnExportar').on('click', async function () {
        let resultado = [];

        if (modo === 'mensual') {
            const mes = $fechaExport.val();
            if (mes) resultado = [mes];
        } else if (modo === 'rango' && rangoSeleccionado) {
            resultado = rangoSeleccionado;
        }

        await exportar_Asistencias(resultado);
    });
});

async function exportar_Asistencias(tiempo) {
    const logoExcelBase64 = `data:image/webp;base64,`;
    try {
        let parametros = {};
        let nombreExport = "";

        if (!(Array.isArray(tiempo))) return alert("Valor inv치lido: 'tiempo' debe ser un arreglo.");
        if (!tiempo) return alert("Seleccione el mes a exportar");
        if (tiempo.length == 1) {
            parametros.mes = tiempo[0];
            nombreExport = (new Date(tiempo[0].split('-'))).toLocaleString('es-PE', { month: 'long', year: 'numeric' });
        } else {
            parametros.fechaIni = tiempo[0];
            parametros.fechaFin = tiempo[1];
            nombreExport = tiempo.join(" a ");
        }

        const params = new URLSearchParams(parametros);
        const response = await fetch(`${__url}/api/asistencias/exportar-mensual?${params}`);
        if (!response.ok) throw new Error(`Error HTTP ${response.status}`);
        const data = await response.json();

        if (data.length === 0) {
            alert("No hay datos para el mes seleccionado");
            return;
        }

        let columnasData = Object.keys(data[0]);
        let columnasNoFecha = [];
        let sheetColumns = [];
        let rowData2 = [];

        columnasData.forEach(e => {
            let cabecera = e.replaceAll('_', ' ').toUpperCase();

            let esFecha = (new Date(e) instanceof Date && !isNaN(new Date(e)));
            if (!esFecha) columnasNoFecha.push(e);

            let anchoFijos = { personal: 50, dni: 18, dscto_tardanza: 20 };
            sheetColumns.push({ header: cabecera, key: e, width: (esFecha ? 14 : (anchoFijos[e] || 15)) });
            rowData2.push(esFecha ? `${obtenerDiaAbreviado(e)}\n${e}` : cabecera);
        });

        const workbook = new ExcelJS.Workbook();
        const sheet = workbook.addWorksheet("Asistencias " + nombreExport);

        // --- M치rgenes de p치gina ---
        sheet.pageSetup.margins = {
            left: 0.7, right: 0.7, top: 0.75, bottom: 0.75,
            header: 0.3, footer: 0.3
        };

        // --- Definir columnas ---
        sheet.columns = sheetColumns;

        let alignment = [
            { col: 9, horizontal: 'right', sangria: 2 },
            { col: 10, horizontal: 'right', sangria: 5 },
            { col: 11, horizontal: 'right', sangria: 8 },
            { col: 12, horizontal: 'right', sangria: 17 },
            { col: 13, horizontal: 'right', sangria: 20 },
            { col: 14, horizontal: 'right', sangria: 23 },
        ].find(c => c.col == columnasData.length) || { col: 0, horizontal: 'center', sangria: 0 };

        // --- T칤tulo principal (merge) ---
        sheet.mergeCells(mergeRange("A1", columnasData.length));
        const title = sheet.getCell("A1");
        title.value = `REPORTE DE ASISTENCIAS - ${nombreExport}`.toUpperCase();
        title.font = { name: "Arial", size: 20, bold: true, color: { argb: "FFFFFFFF" } };
        title.alignment = { vertical: "middle", horizontal: alignment.horizontal, indent: alignment.sangria }; // , indent: 1
        title.fill = { type: "pattern", pattern: "solid", fgColor: { argb: "3b71ca" } };
        sheet.getRow(1).height = 90;

        // --- Llamar a la funci칩n despu칠s de crear hoja y workbook ---
        // Agregar la imagen al workbook
        const imageLogo = workbook.addImage({
            base64: logoExcelBase64,
            extension: 'png',
        });

        // Aqu칤 la colocamos en la esquina superior izquierda (A1:B1)
        sheet.addImage(imageLogo, {
            tl: { col: 0, row: 0.26 },  // top-left (columna, fila)
            ext: { width: 260, height: 65 } // tama침o en p칤xeles
        });

        // --- Encabezados (fila 2) ---
        const rowDias = sheet.addRow(rowData2);
        rowDias.eachCell((cell, colNumber) => {
            cell.font = { name: "Arial", size: 9, bold: true, color: { argb: "FFFFFFFF" } };
            cell.alignment = { vertical: "middle", horizontal: "center", wrapText: true };
            cell.fill = { type: "pattern", pattern: "solid", fgColor: { argb: "4472C4" } };
            cell.border = {
                top: { style: "thin", color: { argb: "13479c" } },
                left: { style: "thin", color: { argb: "13479c" } },
                right: { style: "thin", color: { argb: "13479c" } },
                bottom: { style: "thin", color: { argb: "13479c" } }
            };
        });
        sheet.getRow(2).height = 32;

        // filtro
        sheet.autoFilter = {
            from: 'A2',
            to: 'B2'
        };

        // --- Filas de datos
        data.forEach((p, index) => {
            let rowData = {};

            Object.keys(p).forEach(e => {
                if (columnasNoFecha.includes(e)) {
                    rowData[e] = p[e];
                } else {
                    let txtAsistencia = "";
                    let estado = tipoAsistencia.find(s => s.id === p[e].tipo_asistencia);
                    if (estado) {
                        txtAsistencia = estado.descripcion;
                        if (p[e].hora) txtAsistencia = `${estado.simbolo}\n${p[e].hora}`;
                    }
                    rowData[e] = txtAsistencia;
                }
            });

            const row = sheet.addRow(rowData);
            row.height = 30;
            // estilo general por celda
            row.eachCell((cell, colNumber) => {
                let key = cell._column._key;
                let font = { name: "Arial", size: 9, bold: false, color: { argb: '000000' } };
                let fill = { type: "pattern", pattern: "solid", fgColor: { argb: "FFFFFF" } };

                cell.alignment = { vertical: "middle", horizontal: "center", wrapText: true };
                cell.border = {
                    top: { style: "thin", color: { argb: "13479c" } },
                    left: { style: "thin", color: { argb: "13479c" } },
                    right: { style: "thin", color: { argb: "13479c" } },
                    bottom: { style: "thin", color: { argb: "13479c" } }
                };

                if (columnasNoFecha.includes(key)) {
                    if (key == 'personal') cell.alignment = { vertical: "middle", horizontal: "left", indent: 2 };
                    if (key == 'dscto_tardanza') {
                        cell.numFmt = '_([$S/] * #,##0.00_);_([$S/] * (#,##0.00);_([$S/] * "0.00"??_);_(@_)';
                        font.color['argb'] = cell.value == 0 ? '000000' : 'dc3545';
                    }
                } else {
                    let val = cell.value ? cell.value.toString().split('\n') : "";
                    let estado = tipoAsistencia.find(s => s[val[0].length === 1 ? 'simbolo' : 'descripcion'] == val[0]);
                    if (estado) {
                        font.bold = true;
                        font.color.argb = (estado.color).replace('#', 'FF');
                        fill.fgColor.argb = estado.fill;
                    }
                }

                cell.font = font;
                cell.fill = fill;
            });
        });

        // --- Fila final combinada ---
        sheet.mergeCells(mergeRange(`A${sheet.lastRow.number + 1}`, columnasData.length));
        const finalCell = sheet.getCell(`A${sheet.lastRow.number}`);
        finalCell.value = "Fin del reporte";
        finalCell.alignment = { horizontal: "center" };
        finalCell.font = { italic: true, color: { argb: "FF666666" } };

        // 游댳 Finalmente: congelar filas 1 y 2
        sheet.views = [{ state: 'frozen', xSplit: 2, ySplit: 2 }];

        agregarLeyenda(sheet, (columnasData.length + 2), 3);

        // --- Generar y descargar ---
        const buffer = await workbook.xlsx.writeBuffer();
        const blob = new Blob([buffer], {
            type: "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet"
        });
        saveAs(blob, `asistencias_rci_${nombreExport.replaceAll(' ', '_')}.xlsx`);
    } catch (error) {
        console.error("Error al generar Excel:", error);
        alert("Hubo un problema generando el reporte");
    }
};

/**
 * Crea una mini tabla de leyenda a la derecha del reporte.
 * 
 * @param {Worksheet} sheet - Hoja de ExcelJS
 * @param {number} startCol - Columna inicial donde colocar la tabla (ej. 10)
 * @param {number} startRow - Fila inicial donde colocar la tabla (ej. 2)
 */
function agregarLeyenda(sheet, startCol = 10, startRow = 2) {
    // --- Encabezado ---
    const headerCell = sheet.getCell(startRow, startCol);
    headerCell.value = "LEYENDA DE ASISTENCIA";
    headerCell.font = { bold: true, color: { argb: "FFFFFFFF" }, size: 12 };
    headerCell.alignment = { horizontal: "center", vertical: "middle" };
    headerCell.fill = { type: "pattern", pattern: "solid", fgColor: { argb: "3b71ca" } };

    // Combina celdas del encabezado (2 columnas: s칤mbolo + descripci칩n)
    sheet.mergeCells(startRow, startCol, startRow, startCol + 1);
    // sheet.getRow(startRow).height = 22;

    // --- Ajustes de ancho de columnas ---
    sheet.getColumn(startCol).width = 10;  // s칤mbolo
    sheet.getColumn(startCol + 1).width = 25; // descripci칩n

    // --- Rellenar leyendas ---
    tipoAsistencia.forEach((item, i) => {
        const rowIndex = startRow + i + 1;

        const simboloCell = sheet.getCell(rowIndex, startCol);
        const descripcionCell = sheet.getCell(rowIndex, startCol + 1);

        // Simbolo
        simboloCell.value = item.simbolo;
        simboloCell.font = { bold: true, color: { argb: "FFFFFFFF" } };
        simboloCell.alignment = { horizontal: "center", vertical: "middle" };
        simboloCell.fill = { type: "pattern", pattern: "solid", fgColor: { argb: item.color.replace("#", "") } };

        // Descripci칩n
        descripcionCell.value = item.descripcion;
        descripcionCell.alignment = { horizontal: "left", vertical: "middle", indent: 2 };

        // Bordes
        [simboloCell, descripcionCell].forEach(c => {
            c.border = {
                top: { style: "thin", color: { argb: "999999" } },
                left: { style: "thin", color: { argb: "999999" } },
                bottom: { style: "thin", color: { argb: "999999" } },
                right: { style: "thin", color: { argb: "999999" } },
            };
        });
    });
}

function mergeRange(startCell, numCols) {
    // Separar letras (columna) y n칰meros (fila)
    const match = startCell.match(/^([A-Z]+)(\d+)$/i);
    if (!match) throw new Error("Formato de celda no v치lido (ej: A1)");

    const startColLetters = match[1].toUpperCase();
    const row = parseInt(match[2], 10);

    // Convertir letras a n칰mero de columna (A=1, B=2, ..., Z=26, AA=27...)
    function lettersToNumber(letters) {
        let num = 0;
        for (let i = 0; i < letters.length; i++) {
            num = num * 26 + (letters.charCodeAt(i) - 64);
        }
        return num;
    }

    // Convertir n칰mero a letras (1=A, 27=AA, etc.)
    function numberToLetters(num) {
        let letters = "";
        while (num > 0) {
            const rem = (num - 1) % 26;
            letters = String.fromCharCode(65 + rem) + letters;
            num = Math.floor((num - 1) / 26);
        }
        return letters;
    }

    // Calcular columna final
    const startNum = lettersToNumber(startColLetters);
    const endNum = startNum + (numCols - 1);
    const endLetters = numberToLetters(endNum);

    return `${startColLetters}${row}:${endLetters}${row}`;
}

function obtenerDiaAbreviado(fechaStr) {
    const [year, month, day] = fechaStr.split('-').map(Number);
    // El mes en Date() empieza en 0 (enero = 0)
    const fecha = new Date(year, month - 1, day);

    const dias = ["DO", "LU", "MA", "MI", "JU", "VI", "SA"];
    return dias[fecha.getDay()];
}